import React from "react";

import { NavLink } from "react-router-dom";
//import { CopyToClipboard } from "react-copy-to-clipboard";

import BOG from "../../assets/BOG.jpg";
import FACE from "../../assets/FACE.png";
import NIGHT from '../../assets/NIGHT.JPG'

import "./About.css";

class About extends React.Component {
  componentDidMount() {
    window.scrollTo(0, 0);
  }

  render() {
    return (
      <div className="ContextContainer">
        {/* ---------- */}
        {/* MENU */}
        {/* ---------- */}
        <div className="uk-flex uk-flex-center MenuBar ">
          <NavLink to="/home" activeClassName="ItemSelected">
            <p className="MenuItem">Home</p>
          </NavLink>
          <NavLink to="/about" activeClassName="ItemSelected">
            <p className="MenuItem">About</p>
          </NavLink>
          <NavLink to="/experience" activeClassName="ItemSelected">
            <p className="MenuItem">Experience</p>
          </NavLink>
          <NavLink to="/contact" activeClassName="ItemSelected">
            <p className="MenuItem">Contact</p>
          </NavLink>
        </div>

        <div style={{ height: "2vh" }}>&nbsp;</div>

        {/* ---------- */}
        {/* CONTENT */}
        {/* ---------- */}

        <div className="uk-flex uk-flex-center uk-flex-middle Content">
          <div
            className="BaseCard"
            style={{ backgroundImage: "url(" + BOG + ")" }}
          >
            <h1 id="CardName">about</h1>
          </div>
        </div>

        <div className="uk-flex uk-flex-center">
          <div id="AnimojiCard" className="uk-child-width-1-1">
            <div
              id="IntroPic"
              style={{ backgroundImage: "url(" + FACE + ")" }}
            />
            <br />
            <br />
            <div>
              <h2 id="Intro">
                Hi, I'm Sebastian García. Most of the time, I develop and design
                user experiences. People often simply call them websites,
                applications, or platforms.
              </h2>
            </div>
          </div>
        </div>


        {/* ---------- */}
        {/* Contact */}
        {/* ---------- */}

        <div className="uk-flex uk-flex-center uk-flex-middle Content">
          <div
            className="BaseCard"
            style={{ backgroundImage: "url(" + NIGHT + ")" }}
          >
            <h1 id="CardNameContact">contact</h1>
          </div>
        </div>

        <div className="uk-flex uk-flex-center">
          <div id="ContactCard" className="uk-child-width-1-1">
            goli
          </div>
        </div>



        <div style={{ height: "10vh" }}>&nbsp;</div>
      </div>
    );
  }
}

export default About;
